  if( status_code == 200 ){
    String response = httpClient.getString();

    // M5.Lcd.println("response:");
    // M5.Lcd.println(response);
  }
  httpClient.end();

  delay(2000);
}

void loop() {
  M5.update();

  // センサーから値を取得
  int currentButtonState = digitalRead(digitalPin);

  // 値に変化があれば通知
  if( currentButtonState != buttonState ){
